// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

import "./ItemManager.sol";

contract Item {
    uint256 public priceInWei;
    uint256 public paidWei;
    uint256 public index;

    ItemManager parentContract;

    constructor(
        ItemManager _parentcontract,
        uint256 _priceInWei,
        uint256 _index
    ) {
        priceInWei = _priceInWei;
        index = _index;
        parentContract = _parentcontract;
    }

    receive() external payable {
        require(paidWei == 0, "The item is already paid.");
        require(priceInWei == msg.value, "We don't support partial payments.");
        paidWei += msg.value;
        (bool success, ) = address(parentContract).call{value: msg.value}(
            abi.encodeWithSignature("triggerPayment(uint256)",index)
        );
        require(success, "The transaction was not successful, canceling.");
    }

	// this is bc if someone sends plain ether without proper data this fallback
	// will send back the ether to the user
    fallback() external {}
}
